(function(jQuery){jQuery.fn.pwdMeter=function(options){options=jQuery.extend({minLength:5,displayGeneratePassword:false,generatePassText:'Password Generator',generatePassClass:'GeneratePasswordLink',randomPassLength:13,passwordBox:this},options);return this.each(function(index){$(this).keyup(function(){evaluateMeter();});function evaluateMeter(){var passwordStrength=0;var password=$(options.passwordBox).val();if((password.length>0)&&(password.length<=5))passwordStrength=1;if(password.length>=options.minLength)passwordStrength++;if((password.match(/[a-z]/))&&(password.match(/[A-Z]/)))passwordStrength++;if(password.match(/\d+/))passwordStrength++;if(password.match(/.[!,@,#,$,%,^,&,*,?,_,~,-,(,)]/))passwordStrength++;if(password.length>12)passwordStrength++;$('#pwdMeter').removeClass();$('#pwdMeter').addClass('neutral');switch(passwordStrength){case 1:$('#pwdMeter').addClass('veryweak text-danger');$('#pwdMeter').text('Очень слабый');break;case 2:$('#pwdMeter').addClass('weak text-danger');$('#pwdMeter').text('Слабый');break;case 3:$('#pwdMeter').addClass('medium text-warning');$('#pwdMeter').text('Средний');break;case 4:$('#pwdMeter').addClass('strong text-success');$('#pwdMeter').text('Хорошый');break;case 5:$('#pwdMeter').addClass('verystrong text-success');$('#pwdMeter').text('Хороший');break;default:$('#pwdMeter').addClass('neutral text-danger');$('#pwdMeter').text('Очень слабый');}}
if(options.displayGeneratePassword){$('#pwdMeter').after(' <span id="Spn_PasswordGenerator" class="'+options.generatePassClass+'">'+options.generatePassText+'</span> <span id="Spn_NewPassword" class="NewPassword"></span>');}
$('#Spn_PasswordGenerator').click(function(){var randomPassword=random_password();$('#Spn_NewPassword').text(randomPassword);$(options.passwordBox).val(randomPassword);evaluateMeter();});function random_password(){var allowed_chars="0123456789ABCDEFGHIJKLMNOPQRSTUVWXTZabcdefghiklmnopqrstuvwxyz!?$?%^&*()_-+={[}]:;@~#|\<,>.?/";var pwd_length=options.randomPassLength;var rnd_pwd='';for(var i=0;i<pwd_length;i++){var rnd_num=Math.floor(Math.random()*allowed_chars.length);rnd_pwd+=allowed_chars.substring(rnd_num,rnd_num+1);}
return rnd_pwd;}});}})(jQuery)
